import React, { useState, useEffect } from "react";
import { fetchData } from "../../api/fetchApi";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import styles from "../table/table.css";


const Tabular = () => {

    const [fetchedState, setFetchedState] = useState([])

    useEffect(() => {
        const fetchAPIState = async () => {
            const dailyData = await fetchData()
            setFetchedState(dailyData)
            console.log(dailyData)
        }
        fetchAPIState()
    }, [])

    return (
        <>
            <TableContainer component={Paper}>
                <Table className={styles.container} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell className="alert-secondary">State</TableCell>
                            <TableCell className="alert-warning" align="right">Infected</TableCell>
                            <TableCell className="alert-success" align="right">Discharged</TableCell>
                            <TableCell className="alert-danger" align="right">Deaths</TableCell>
                        </TableRow>
                    </TableHead>
                    {/* Tabular data displaying data from all states of India */}
                    <TableBody>
                        <TableCell component="th" scope="row">
                            {fetchedState.country.map((e, i) => (
                                <h1> {e.Country}</h1>
                            ))}
                        </TableCell>
                        {/* {fetchedState.map(({ country }, i) => (
                            <TableRow key={i}>
                                <TableCell component="th" scope="row">
                                    {country.Country}
                                </TableCell>
                                <TableCell style={{ background: 'rgba(255, 241, 194, 0.5)' }} align="right">{country.TotalConfirmed}</TableCell>
                                <TableCell style={{ background: 'rgba(203, 234, 209, 0.5)' }} align="right">{country.TotalRecovered}</TableCell>
                                <TableCell style={{ background: 'rgba(246, 204, 209, 0.5)' }} align="right">{country.TotalDeaths}</TableCell>
                            </TableRow>
                        ))} */}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    )

}
export default Tabular
